alter table TMTB_TXN_REVIEW_REQ_FLOW modify REMARKS null;
update MSTB_MASTER_TABLE set MASTER_VALUE = '<P style="font-family:Calibri; font-size:16px;white-space: pre">Dear #userName#,<br>The Trip Change Request you submitted has been approved.<br>The details are as follows:<br>&nbsp;&nbsp;&nbsp;&nbsp;Account No: #custNo#<br>&nbsp;&nbsp;&nbsp;&nbsp;Account Name: #acctName#<br>&nbsp;&nbsp;&nbsp;&nbsp;Job No: #jobNo#<br><br>To check your Trip Change Request, please <a href="http://localhost:8080/ibs">click this link</a> to login to the System<br><br>Thanks and Best Regards.</P>' where master_type = 'EMTRA' and master_code = 'CONT';
update MSTB_MASTER_TABLE set MASTER_VALUE = '<P style="font-family:Calibri; font-size:16px;white-space: pre">Dear #userName#,<br>The Trip Change Request you submitted has been rejected.<br>The details are as follows:<br>&nbsp;&nbsp;&nbsp;&nbsp;Account No: #custNo#<br>&nbsp;&nbsp;&nbsp;&nbsp;Account Name: #acctName#<br>&nbsp;&nbsp;&nbsp;&nbsp;Job No: #jobNo#<br><br>To check your Trip Change Request, please <a href="http://localhost:8080/ibs">click this link</a> to login to the System<br><br>Thanks and Best Regards.</P>' where master_type = 'EMTRR' and master_code = 'CONT';